import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { sweetsAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import SweetCard from './SweetCard';

const Dashboard = () => {
  const [sweets, setSweets] = useState([]);
  const [filteredSweets, setFilteredSweets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });

  const { user, isAdmin, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchSweets();
  }, []);

  useEffect(() => {
    filterSweets();
  }, [searchTerm, categoryFilter, priceRange, sweets]);

  const fetchSweets = async () => {
    try {
      setLoading(true);
      const response = await sweetsAPI.getAll();
      setSweets(response.data);
      setFilteredSweets(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch sweets');
    } finally {
      setLoading(false);
    }
  };

  const filterSweets = () => {
    let filtered = [...sweets];
    if (searchTerm) {
      filtered = filtered.filter(sweet =>
        sweet.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        sweet.category.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    if (categoryFilter) {
      filtered = filtered.filter(
        sweet => sweet.category.toLowerCase() === categoryFilter.toLowerCase()
      );
    }
    if (priceRange.min) {
      filtered = filtered.filter(sweet => sweet.price >= parseFloat(priceRange.min));
    }
    if (priceRange.max) {
      filtered = filtered.filter(sweet => sweet.price <= parseFloat(priceRange.max));
    }
    setFilteredSweets(filtered);
  };

  const handlePurchase = async (sweetId, quantity) => {
    try {
      await sweetsAPI.purchase(sweetId, quantity);
      fetchSweets();
    } catch (err) {
      alert(err.response?.data?.message || 'Purchase failed');
    }
  };

  const categories = [...new Set(sweets.map(sweet => sweet.category))];

  return (
    <div className="min-h-screen flex flex-col bg-pink-50">
      {/* HEADER */}
      <header className="sticky top-0 z-50 bg-gradient-to-r from-pink-600 to-pink-800 text-white shadow-md py-5">
        <div className="w-full max-w-[1600px] mx-auto flex flex-col md:flex-row justify-between items-center gap-4 md:gap-0 px-6 md:px-12">
          <img
            src="https://tse1.mm.bing.net/th/id/OIP.BbHcLHRHQicFv98-agHHUQHaHa?pid=Api&P=0&h=180"
            alt="SweetShop Logo"
            className="w-24 h-24 rounded-full object-cover border-4 border-white shadow-lg"
          />
          <div className="flex flex-wrap items-center gap-4 md:gap-6">
            <span className="font-medium text-lg">Welcome, {user?.username}!</span>
            {isAdmin() && (
              <button
                onClick={() => navigate('/admin')}
                className="bg-pink-200 text-pink-700 px-3 py-1 rounded-full font-bold text-sm hover:bg-pink-300 transition"
              >
                Admin Panel
              </button>
            )}
            <button
              onClick={logout}
              className="bg-gray-200 text-gray-700 px-4 py-2 rounded hover:bg-gray-300 transition"
            >
              Logout
            </button>
          </div>
        </div>
      </header>

      {/* MAIN CONTENT */}
      <main className="flex-1 w-full max-w-[1600px] mx-auto p-6 flex flex-col gap-6">
        {/* FILTERS */}
        <div className="bg-white p-6 rounded-xl border border-pink-200 shadow-md w-full flex flex-col gap-4 md:gap-6">
          <input
            type="text"
            placeholder="Search sweets by name or category..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full p-3 border-2 border-orange-800 rounded-lg focus:outline-none focus:ring-2 focus:ring-pink-400"
          />
          <div className="flex flex-wrap gap-4 items-center">
            <select
              value={categoryFilter}
              onChange={(e) => setCategoryFilter(e.target.value)}
              className="p-2 border-2 border-orange-800 rounded-lg"
            >
              <option value="">All Categories</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
            <input
              type="number"
              placeholder="Min Price"
              value={priceRange.min}
              onChange={(e) => setPriceRange({ ...priceRange, min: e.target.value })}
              className="p-2 border-2 border-orange-800 rounded-lg w-28"
            />
            <input
              type="number"
              placeholder="Max Price"
              value={priceRange.max}
              onChange={(e) => setPriceRange({ ...priceRange, max: e.target.value })}
              className="p-2 border-2 border-orange-800 rounded-lg w-28"
            />
            <button
              onClick={() => {
                setSearchTerm('');
                setCategoryFilter('');
                setPriceRange({ min: '', max: '' });
              }}
              className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-md font-semibold transition"
            >
              Clear Filters
            </button>
          </div>
        </div>

        {/* LOADING / ERROR */}
        {loading && (
          <div className="text-center text-pink-600 py-10 bg-white rounded-xl shadow-md w-full">
            Loading sweets...
          </div>
        )}
        {error && (
          <div className="text-center text-pink-600 py-10 bg-white rounded-xl shadow-md w-full">
            {error}
          </div>
        )}

        {/* SWEETS GRID */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 w-full">
          {filteredSweets.length === 0 && !loading && (
            <div className="text-center text-pink-700 col-span-full">No sweets found</div>
          )}
          {filteredSweets.map(sweet => (
            <SweetCard
              key={sweet._id}
              sweet={sweet}
              isAdmin={false}
              onPurchase={handlePurchase}
              onUpdated={fetchSweets}
            />
          ))}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
