import React, { useState } from 'react';
import { motion } from 'framer-motion';
import EditSweetModal from './EditSweetModal';

const SweetCard = ({ sweet, isAdmin, onPurchase, onDelete, onRestock, onUpdated, showPurchase = true }) => {
  const [purchaseQuantity, setPurchaseQuantity] = useState(1);
  const [restockQuantity, setRestockQuantity] = useState(10);
  const [showRestockInput, setShowRestockInput] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);

  const handlePurchase = () => {
    if (purchaseQuantity > 0 && purchaseQuantity <= sweet.quantity) {
      onPurchase(sweet._id, purchaseQuantity);
      setPurchaseQuantity(1);
    }
  };

  const handleRestock = () => {
    if (restockQuantity > 0) {
      onRestock(sweet._id, restockQuantity);
      setRestockQuantity(10);
      setShowRestockInput(false);
    }
  };

  return (
    <motion.div
      whileHover={{ scale: 1.05, boxShadow: "0 20px 30px rgba(216, 27, 96, 0.3)" }}
      transition={{ type: "spring", stiffness: 300 }}
      className="bg-white border-2 border-pink-200 rounded-2xl shadow-md overflow-hidden transform transition-all"
      style={{ minWidth: '320px', maxWidth: '400px', minHeight: '480px' }}
    >
      {/* Image */}
      <div className="w-full h-64 bg-gradient-to-tr from-pink-200 to-pink-100 flex items-center justify-center">
        {sweet.imageUrl ? (
          <img src={sweet.imageUrl} alt={sweet.name} className="w-full h-full object-cover" />
        ) : (
          <div className="text-8xl text-white/90">üç¨</div>
        )}
      </div>

      {/* Details */}
      <div className="p-6 space-y-4">
        <h3 className="text-pink-600 text-2xl font-bold">{sweet.name}</h3>
        <p className="text-pink-700 text-sm font-semibold tracking-wide">{sweet.category}</p>
        {sweet.description && (
          <p className="text-gray-600 text-sm leading-relaxed">{sweet.description}</p>
        )}

        <div className="flex justify-between items-center mb-3">
          <span className="text-pink-600 font-bold text-xl">${sweet.price.toFixed(2)}</span>
          <span className={`text-sm font-medium ${sweet.quantity === 0 ? 'text-red-500' : 'text-green-600'}`}>
            {sweet.quantity > 0 ? `${sweet.quantity} in stock` : 'Out of stock'}
          </span>
        </div>

        {/* Actions */}
        <div className="flex flex-col gap-3">
          {showPurchase && sweet.quantity > 0 && (
            <div className="flex gap-3 items-center">
              <input
                type="number"
                min="1"
                max={sweet.quantity}
                value={purchaseQuantity}
                onChange={(e) => setPurchaseQuantity(parseInt(e.target.value) || 1)}
                className="w-20 px-3 py-2 border-2 border-gray-300 rounded-lg focus:border-pink-600 outline-none text-center"
              />
              <button
                onClick={handlePurchase}
                disabled={sweet.quantity === 0}
                className="bg-pink-600 hover:bg-pink-700 text-white px-5 py-2 rounded-lg font-semibold transition-all disabled:opacity-60"
              >
                Purchase
              </button>
            </div>
          )}

          {isAdmin && (
            <div className="flex flex-col gap-2">
              <button
                onClick={() => setShowEditModal(true)}
                className="bg-gray-300 text-gray-700 hover:bg-gray-400 px-5 py-2 rounded-lg font-semibold transition-all"
              >
                Edit
              </button>

              {!showRestockInput ? (
                <button
                  onClick={() => setShowRestockInput(true)}
                  className="bg-green-500 text-white hover:bg-green-600 px-5 py-2 rounded-lg font-semibold transition-all"
                >
                  Restock
                </button>
              ) : (
                <div className="flex gap-2 items-center">
                  <input
                    type="number"
                    min="1"
                    value={restockQuantity}
                    onChange={(e) => setRestockQuantity(parseInt(e.target.value) || 10)}
                    className="w-20 px-3 py-2 border-2 border-gray-300 rounded-lg focus:border-green-600 outline-none text-center"
                  />
                  <button
                    onClick={handleRestock}
                    className="bg-green-500 text-white hover:bg-green-600 px-3 py-1 rounded-lg font-semibold transition-all"
                  >
                    ‚úì
                  </button>
                  <button
                    onClick={() => setShowRestockInput(false)}
                    className="bg-gray-300 text-gray-700 hover:bg-gray-400 px-3 py-1 rounded-lg font-semibold transition-all"
                  >
                    ‚úï
                  </button>
                </div>
              )}

              <button
                onClick={() => onDelete(sweet._id)}
                className="bg-red-500 hover:bg-red-600 text-white px-5 py-2 rounded-lg font-semibold transition-all"
              >
                Delete
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Edit Modal */}
      {showEditModal && (
        <EditSweetModal
          sweet={sweet}
          onClose={() => setShowEditModal(false)}
          onSuccess={() => {
            setShowEditModal(false);
            if (typeof onUpdated === 'function') onUpdated();
          }}
        />
      )}
    </motion.div>
  );
};

export default SweetCard;
