import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { sweetsAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import SweetCard from './SweetCard';

const Dashboard = () => {
  const [sweets, setSweets] = useState([]);
  const [filteredSweets, setFilteredSweets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });

  const { user, isAdmin, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchSweets();
  }, []);

  useEffect(() => {
    filterSweets();
  }, [searchTerm, categoryFilter, priceRange, sweets]);

  const fetchSweets = async () => {
    try {
      setLoading(true);
      const response = await sweetsAPI.getAll();
      setSweets(response.data);
      setFilteredSweets(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch sweets');
    } finally {
      setLoading(false);
    }
  };

  const filterSweets = () => {
    let filtered = [...sweets];
    if (searchTerm) {
      filtered = filtered.filter(sweet =>
        sweet.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        sweet.category.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
    if (categoryFilter) {
      filtered = filtered.filter(
        sweet => sweet.category.toLowerCase() === categoryFilter.toLowerCase()
      );
    }
    if (priceRange.min) {
      filtered = filtered.filter(sweet => sweet.price >= parseFloat(priceRange.min));
    }
    if (priceRange.max) {
      filtered = filtered.filter(sweet => sweet.price <= parseFloat(priceRange.max));
    }
    setFilteredSweets(filtered);
  };

  const handlePurchase = async (sweetId, quantity) => {
    try {
      await sweetsAPI.purchase(sweetId, quantity);
      fetchSweets();
    } catch (err) {
      alert(err.response?.data?.message || 'Purchase failed');
    }
  };

  const categories = [...new Set(sweets.map(sweet => sweet.category))];
  const totalSweets = sweets.length;
  const categoriesCount = categories.length;

  return (
    <div className=" bg-pink-50">
      {/* HEADER */}
      <header className="bg-gradient-to-r from-pink-700 to-pink-900 text-white shadow-md">
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center justify-between pl-3 p-6 md:p-8 gap-6">
          {/* Left: Logo + Title */}
          <div className="flex items-center gap-4">
            <img
              src="https://tse1.mm.bing.net/th/id/OIP.BbHcLHRHQicFv98-agHHUQHaHa?pid=Api&P=0&h=180"
              alt="SweetShop Logo"
              className="w-24 h-24 rounded-full object-cover border-4 border-white shadow-lg"
            />
            <div className="flex flex-col">
              <h1 className="text-2xl font-bold">Sweet Shop</h1>
              <p className="text-sm opacity-90">Handmade treats & delightful bites â€” fresh every day</p>
            </div>
          </div>

          {/* Center: Stats */}
          <div className="hidden md:flex items-center gap-6">
            <div className="bg-white text-pink-800 px-4 py-2 rounded-lg shadow-sm text-center">
              <div className="text-xl font-bold">{totalSweets}</div>
              <div className="text-xs">Total Sweets</div>
            </div>
            <div className="bg-white text-pink-800 px-4 py-2 rounded-lg shadow-sm text-center">
              <div className="text-xl font-bold">{categoriesCount}</div>
              <div className="text-xs">Categories</div>
            </div>
          </div>

          {/* Right: User info & actions */}
          <div className="flex flex-col md:flex-row items-center gap-4">
            <div className="text-right md:text-left">Welcome, <span className="font-semibold">{user?.username}</span>!</div>
            {isAdmin() && (
              <>
                <span className="bg-pink-200 text-pink-700 px-3 py-1 rounded-full text-sm font-semibold">
                  Admin
                </span>
                <button
                  onClick={() => navigate('/admin')}
                  className="bg-pink-600 hover:bg-pink-700 px-4 py-2 rounded-md font-semibold transition"
                >
                  Admin Panel
                </button>
              </>
            )}
            <button
              onClick={logout}
              className="bg-white text-pink-800 px-4 py-2 rounded-md hover:bg-pink-50 transition font-semibold"
            >
              Logout
            </button>
          </div>
        </div>
      </header>

      {/* CONTENT */}
      <main className="max-w-6xl mx-auto p-6">
        {/* FILTERS */}
        <div className="bg-white p-6 rounded-xl border border-pink-200 shadow-md mb-6">
          <div className="mb-4">
            <input
              type="text"
              placeholder="Search sweets by name or category..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full p-3 border-2  border-orange-800  rounded-lg focus:outline-none focus:ring-2 focus:ring-pink-400"
            />
          </div>
          <div className="flex flex-wrap gap-4 items-center">
            <select
              value={categoryFilter}
              onChange={(e) => setCategoryFilter(e.target.value)}
              className="p-2 border-2 border-orange-800 rounded-lg"
            >
              <option value="">All Categories</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
            <input
              type="number"
              placeholder="Min Price"
              value={priceRange.min}
              onChange={(e) => setPriceRange({ ...priceRange, min: e.target.value })}
              className="p-2 border-2  border-orange-800  rounded-lg w-28"
            />
            <input
              type="number"
              placeholder="Max Price"
              value={priceRange.max}
              onChange={(e) => setPriceRange({ ...priceRange, max: e.target.value })}
              className="p-2 border-2  border-orange-800  rounded-lg w-28"
            />
            <button
              onClick={() => {
                setSearchTerm('');
                setCategoryFilter('');
                setPriceRange({ min: '', max: '' });
              }}
              className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-md font-semibold transition"
            >
              Clear Filters
            </button>
          </div>
        </div>

        {/* LOADING / ERROR */}
        {loading && <div className="text-center text-pink-700 py-10">Loading sweets...</div>}
        {error && <div className="text-center text-pink-700 py-10">{error}</div>}

        {/* SWEETS GRID */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {filteredSweets.length === 0 && !loading && (
            <div className="text-center text-pink-700 col-span-full">No sweets found</div>
          )}
          {filteredSweets.map(sweet => (
            <SweetCard
              key={sweet._id}
              sweet={sweet}
              isAdmin={false}
              onPurchase={handlePurchase}
              onUpdated={fetchSweets}
            />
          ))}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
